        }
        if ("content" in n) {
            var A = n.content.$t
        } else if ("summary" in n) {
            var A = n.summary.$t
        } else var A = "";
        var O = /<\S[^>]*>/g;
        A = A.replace(O, "");
        if (showpostsummary == true) {
