$(document).ready(function() {
	$(".note").click(function() {
		$(".sharebox").fadeToggle(1e3)
	});
	$(window).scroll(function() {
		var e = $(this),
			t = $("#header-wrapper");
		if(e.scrollTop() > 80) {
			t.addClass("sticky_header")
		} else {
			t.removeClass("sticky_header")
		}
	});
	$(".tweetbox").parent(".post-outer").addClass("hasTweet");
	$(".quotebox").parent(".post-outer").addClass("hasQuote");
	$(".widget-content").each(function() {
		var e = $(this).text();
		if(e.substr(0, 10).match("fbbox")) {
			e = e.replace("fbbox/", "");
			$(this).html('<iframe allowtransparency="true" frameborder="0" scrolling="no" src="//www.facebook.com/plugins/likebox.php?href=' + e + '&width=340px&height=258&colorscheme=light&show_faces=true&header=false&stream=false&show_border=false&appId=1760806057479925" style="border:none; overflow:hidden; width:100%; height:258px;"></iframe>')
		}
	});
	var e = $("#sidetabs #tabside1 .widget h2").text();
	$(".menu-tab .item-1 a").text(e);
	var t = $("#sidetabs #tabside2 .widget h2").text();
	$(".menu-tab .item-2 a").text(t);
	var n = $("#sidetabs #tabside3 .widget h2").text();
	$(".menu-tab .item-3 a").text(n);
	$("#tabside1 .widget h2,#tabside2 .widget h2,#tabside3 .widget h2,#tabside1 .widget-title,#tabside2 .widget-title,#tabside3 .widget-title").remove();
	$(".sidetabs").tabslet({
		mouseevent: "click",
		attribute: "href",
		animation: true
	});
	if($(".related-box").length !== 0) {
		$(function() {
			$(".related-tom").jCarouselLite({
				btnNext: ".next2",
				btnPrev: ".prev2",
				visible: 2,
				start: 0,
				auto: 1e4
			})
		})
	}
	if($(".related-box").length === 0) {
		$("#related-posts").remove()
	}
	var r = function(e) {
		e = $.extend({
			length: 200,
			ellipsis: ""
		}, e);
		$(".label_title").each(function() {
			var t = $(this);
			var n = t.text();
			var r = n.substring(0, e.length) + e.ellipsis;
			t.text(r)
		})
	};
	r();
	$(".menu li").each(function() {
		$(this).hoverTimeout(0, function() {
			$(this).children("ul").slideDown()
		}, 0, function() {
			$(this).children("ul").hide()
		})
	});
	if($(".slider .recent-box").length !== 0) {
		$(function() {
			$(".slider-label").jCarouselLite({
				btnNext: ".next",
				btnPrev: ".prev",
				visible: 3,
				scroll: 1,
				start: 1,
				auto: 5e3
			})
		})
	}
	if($(".slider-side .recent-box").length !== 0) {
		$(function() {
			$(".slider-side").jCarouselLite({
				btnNext: ".next3",
				btnPrev: ".prev3",
				visible: 1,
				scroll: 1,
				start: 1,
				auto: 1e4
			})
		})
	}
	var i = document.querySelector(".index .blog-posts, .archive .blog-posts");
	var s = new Masonry(i, {
		itemSelector: ".post-outer"
	});
	$(function() {
		$(window).scroll(function() {
			if($(this).scrollTop() > 100) {
				$(".gototop").fadeIn()
			} else {
				$(".gototop").fadeOut()
			}
		});
		$(".gototop").click(function() {
			$("body,html").animate({
				scrollTop: 0
			}, 800);
			return false
		})
	})
});

// Resize Image to Parent Container - https://github.com/levymetal/jquery-resize-image-to-parent/
(function(e){e.fn.resizeToParent=function(t){function r(e){e.css({width:"",height:"","margin-left":"","margin-top":""});var n=e.parents(t.parent).width();var r=e.parents(t.parent).height();var i=e.width();var s=e.height();var o=i/n;if(s/o<r){e.css({width:"auto",height:r});i=i/(s/r);s=r}else{e.css({height:"auto",width:n});i=n;s=s/o}var u=(i-n)/-2;var a=(s-r)/-2;e.css({"margin-left":u,"margin-top":a})}var n={parent:"div",delay:100};var t=e.extend(n,t);var i;var s=this;e(window).on("resize",function(){clearTimeout(i);i=setTimeout(function(){s.each(function(){r(e(this))})},t.delay)});return this.each(function(){var t=e(this);t.attr("src",t.attr("src"));t.load(function(){r(t)});if(this.complete){r(t)}})}})(jQuery);

